  const CreateChatRoom = async () => {};

  return (
    <Context.Provider
      value={{
        user,
        rooms,
        openRoom,
        fetchRooms,
        CreateChatRoom
      }}
    >
      {children}
    </Context.Provider>
  )
}

export default GlobalState;
