import React,{  useContext,useEffect } from 'react';
import firestore from '../firebase';

import Message from './Message';
import { Context } from '../context/GlobalState';

const ChatWindow = props => {
  const {messages, addMessages, openRoom} = useContext(Context);
  useEffect(() => {
    const element = document.getElementById("window");
    element.scrollTop = element.scrollHeight;
    console.log(Object.keys(openRoom));
    if(!Object.keys(openRoom).length === 0){
      firestore.collection('rooms').doc(openRoom.id).collection('messages')
      .orderBy('date').onSnapshot(snapshot => {
      const changes = snapshot.docChanges();
      const messagesArray = changes.map(item => {
        return item.doc.data();
      });
      addMessages(messagesArray);
      console.log(messagesArray);
      });
    }
  })

  return (
    <div className="chat-window" id='window'>
      {messages.map(item => <Message item={item} />)}
    </div>
  )
}


export default ChatWindow
