import React, { useContext, useState } from 'react';

import { Context } from '../context/GlobalState';

const SendMessage = props => {

  const [input, setInput] = useState('');
  const { openRoom, sendMessage } = useContext(Context);

  const handleSubmit = e => {
    e.preventDefault();
    if(input !== ''){
      sendMessage(input);
      setInput('');
    }
  }
  return openRoom.hasOwnProperty('id') ? (
      <form onSubmit={e => handleSubmit(e)} className='text-box'>
        <input  
          type="text"
          placeholder="Send message"
          value={input}
          onChange={e => setInput(e.target.value)}
        /> 
        <button id="send-button"><div>Icons made by <a href="https://www.flaticon.com/authors/google" title="Google">Google</a> from <a href="https://www.flaticon.com/" 			    title="Flaticon">www.flaticon.com</a> is licensed by <a href="http://creativecommons.org/licenses/by/3.0/" 			    title="Creative Commons BY 3.0" target="_blank">CC 3.0 BY</a></div></button>
      </form>
  )
: <div className='text-box'></div>
}

export default SendMessage
