import React from 'react';

import { Context } from '../context/GlobalState';

const ChatWindow = props => {
  return (
   <Context.Consumer>
    {context => 
      <div>
        <span>{context.openRoom.author} </span>
        <span>{context.openRoom.text}</span>
      </div>
    }
   </Context.Consumer>
  )
}

export default ChatWindow
