import React from 'react';

import { Context } from '../context/GlobalState';

const ChatWindow = props => {
  return (
   <Context.Consumer>
    {context =>( 
      <div className="chat-window">
        {context.messages.map(item => (
          <div>
            <span>{item.author}</span>
            <br/>
            <span>{item.text}</span>
          </div>)
        )}
      </div>
    )}
   </Context.Consumer>
  )
}

export default ChatWindow
