export const ADD_ROOMS = 'add_rooms';
export const ADD_SINGLE_ROOM = 'add_single_room';

export const roomsReducer = (state, action) => {
  switch(action.type) {
    case ADD_ROOMS:
      return [...state, ...action.rooms];
    case ADD_SINGLE_ROOM:
      return state
    default:
      return state;
  }
}
