import React, {useEffect ,useContext } from 'react';

import { Context } from '../context/GlobalState';
import db from '../firebase';

const Rooms = props => {

  const { fetchRooms, handleSetRooms } = useContext(Context);

  useEffect(() => {
    db.collection('rooms').onSnapshot(snapshot => {
      const changes = snapshot.docChanges();
      const snapArray = changes.map(item => item.doc.data());
      handleSetRooms(snapArray);
    });
  },[])

  return (
    <Context.Consumer>
      {context => (
        <div className='rooms-column'>
          {context.rooms.map(item => <div>{item.name}</div>)}
        </div>
      )}
    </Context.Consumer>
  )
}

export default Rooms;
