import React, { useState } from 'react';

import { Context } from '../context/GlobalState';

const Username = props => {
  return (
    <div>
      <Context.Consumer>
        {context => 
          <div className="titles">
            {context.user}
          </div>
        }
      </Context.Consumer>
    </div>
  )
}

export default Username;
