import React from 'react';
import Avatar from 'react-avatar';

const Message = ({ item }) => {
  const date = new Date(item.date)
  const hour = date.getHours();
  const min = date.getMinutes();
  return (
    <div className='message'>
    <Avatar 
            color={Avatar.getRandomColor(Avatar.getRandomColor('sitebase', ['red', 'green', 'blue']))} 
            name={item.author} size="40" 
          />
      <div>
        <span className='author'>{item.author}</span> | <span className='author'>{`${hour}:${min}`}</span>
        <br/>
        <span className='message-text'>{item.text}</span>
      </div>
    </div>
  )
}

export default Message;
